mapper.select:
select count(1) from t_user tuser left join t_role trole on tuser.roleid = trole.roleid
and ( tuser.userid like CONCAT('%',#{userid},'%') or tuser.username like CONCAT('%',#{userid},'%') )
and tuser.roleid = #{roleid}

select trole.rolename as rolename, trole.roledesc as roledesc, tuser.userid, tuser.password, tuser.username, tuser.userFace, tuser.roleid from t_user tuser left join t_role trole on tuser.roleid = trole.roleid
and ( tuser.userid like CONCAT('%',#{userid},'%') or tuser.username like CONCAT('%',#{userid},'%') )
and tuser.roleid = #{roleid}
