mapper.select:
select count(1) from t_logger

	
		and userid in (select userid from t_user where userid like '%' +#{userid} + '%' or username like '%'
		+#{userid} + '%')
	
	
		and logtype = #{logtype}
	
	
		and editdate > #{createtime_start}
	
	
		and editdate < #{createtime_end}

select * from(
select
ROW_NUMBER() OVER(Order by ${sortfield} ${sorttype} ) AS RowNumber,

from t_logger

	
		and userid in (select userid from t_user where userid like '%' +#{userid} + '%' or username like '%'
		+#{userid} + '%')
	
	
		and logtype = #{logtype}
	
	
		and editdate > #{createtime_start}
	
	
		and editdate < #{createtime_end}
	

) as b

	where RowNumber between #{searchPosition} and
	(#{searchPosition}+#{limit})

mapper.delete:
delete from t_logger
where userid in(

	#{item}

)
